import React, { useState } from "react";
import QuoteForm from "../components/QuoteForm";
import Navbar from "../components/Navbar";
import "../styles/AboutContactPage.css";

function AboutContactPage() {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className="about-contact-page">
      {/* Header */}
      <header className="header">
        <div className="header-top">
          <div className="logo-container" onClick={() => window.location.href = "/"}>
            <img src="/assets/logo/Wolf2.png" alt="Logo" className="logo" />
            <img src="/assets/logo/title.png" alt="Title" className="title-image" />
          </div>
          <Navbar />
        </div>
      </header>

      {/* About Section */}
      <section className="about-section">
        <h2>About Cold Company</h2>
        <p>
          Cold Company is your trusted partner for refrigeration, cold rooms,
          aircon installations, and appliance parts. We provide high-quality
          products, expert advice, and outstanding service to keep your
          equipment running smoothly.
        </p>
      </section>

      {/* Contact Section */}
      <section className="contact-section">
        <h2>Contact Us</h2>
        <p><strong>Email:</strong> Herculesmulder94@gmail.com</p>
        <p><strong>Phone:</strong>  +1(940) 764-1751</p>
        <p><strong>Address:</strong> 123 Cold Street, Pretoria, South Africa</p>

        <button
          className="contact-form-btn"
          onClick={() => setShowForm(true)}
        >
          Send a Message / Request Quote
        </button>
      </section>

      {/* Quote / Contact Form Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button
              className="close-modal-btn"
              onClick={() => setShowForm(false)}
            >
              &times;
            </button>
            <QuoteForm onSuccess={() => setShowForm(false)} />
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <p>Â© {new Date().getFullYear()} Cold Company. All rights reserved.</p>
        <p>
          <strong>Email:</strong> info@H&Aappliances@gmail.com/Herculesmulder94@gmail.com | <strong>Phone:</strong> +27 12 345 6789
        </p>
      </footer>
    </div>
  );
}

export default AboutContactPage;
