import React, { useState } from "react";
import axios from "axios";

function QuoteForm() {
  const [step, setStep] = useState(1);
  const [form, setForm] = useState({
    services: [],
    length: "",
    width: "",
    height: "",
    temperature: "",
    acRoomSize: "",
    acType: "",
    maintenanceType: "",
    maintenanceFreq: "",
    name: "",
    email: "",
    phone: "",
    location: "",
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setForm((prev) => ({
        ...prev,
        services: checked
          ? [...prev.services, value]
          : prev.services.filter((s) => s !== value),
      }));
    } else {
      setForm((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/quote", form);
      alert("Quote request submitted! ColdCompany will email you back.");
    } catch (err) {
      console.error(err);
      alert("Error submitting form.");
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-sky-600">Request a Quote</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Step 1 - Select Services */}
        {step === 1 && (
          <div>
            <h3 className="font-semibold mb-2">What services do you need?</h3>
            {["Cold Room", "Mobile Unit", "Aircon Installation", "Maintenance"].map(
              (srv) => (
                <label key={srv} className="block mb-1">
                  <input
                    type="checkbox"
                    value={srv}
                    checked={form.services.includes(srv)}
                    onChange={handleChange}
                    className="mr-2"
                  />
                  {srv}
                </label>
              )
            )}
            <button
              type="button"
              className="mt-4 bg-sky-500 text-white px-4 py-2 rounded"
              onClick={() => setStep(2)}
            >
              Next
            </button>
          </div>
        )}

        {/* Step 2 - Extra Details */}
        {step === 2 && (
          <div className="space-y-4">
            {form.services.includes("Cold Room") || form.services.includes("Mobile Unit") ? (
              <>
                <h3 className="font-semibold">Cold Room / Mobile Details</h3>
                <input
                  type="number"
                  name="length"
                  placeholder="Length (m)"
                  value={form.length}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
                <input
                  type="number"
                  name="width"
                  placeholder="Width (m)"
                  value={form.width}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
                <input
                  type="number"
                  name="height"
                  placeholder="Height (m)"
                  value={form.height}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
                <input
                  type="text"
                  name="temperature"
                  placeholder="Desired Temperature (°C)"
                  value={form.temperature}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
              </>
            ) : null}

            {form.services.includes("Aircon Installation") && (
              <>
                <h3 className="font-semibold">Aircon Details</h3>
                <input
                  type="number"
                  name="acRoomSize"
                  placeholder="Room Size (m²)"
                  value={form.acRoomSize}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
                <input
                  type="text"
                  name="acType"
                  placeholder="AC Type (Split / Ducted / Portable)"
                  value={form.acType}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
              </>
            )}

            {form.services.includes("Maintenance") && (
              <>
                <h3 className="font-semibold">Maintenance Details</h3>
                <input
                  type="text"
                  name="maintenanceType"
                  placeholder="System Type (Cold room / AC / Freezer)"
                  value={form.maintenanceType}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
                <input
                  type="text"
                  name="maintenanceFreq"
                  placeholder="Frequency (Monthly / Quarterly)"
                  value={form.maintenanceFreq}
                  onChange={handleChange}
                  className="w-full border p-2 rounded"
                />
              </>
            )}

            <button
              type="button"
              className="mt-4 bg-sky-500 text-white px-4 py-2 rounded"
              onClick={() => setStep(3)}
            >
              Next
            </button>
          </div>
        )}

        {/* Step 3 - Contact Info */}
        {step === 3 && (
          <div className="space-y-4">
            <h3 className="font-semibold">Your Contact Info</h3>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={form.name}
              onChange={handleChange}
              className="w-full border p-2 rounded"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              className="w-full border p-2 rounded"
              required
            />
            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              value={form.phone}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
            <input
              type="text"
              name="location"
              placeholder="Location"
              value={form.location}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
            <button
              type="submit"
              className="bg-green-600 text-white px-4 py-2 rounded"
            >
              Submit Request
            </button>
          </div>
        )}
      </form>
    </div>
  );
}

export default QuoteForm;
