import React, { useEffect, useRef } from "react";

function AdCarousel() {
  const carouselRef = useRef(null);

  useEffect(() => {
    const scroll = () => {
      if (carouselRef.current) {
        const { scrollLeft, scrollWidth, clientWidth } = carouselRef.current;
        if (scrollLeft + clientWidth >= scrollWidth) {
          carouselRef.current.scrollTo({ left: 0, behavior: "smooth" });
        } else {
          carouselRef.current.scrollBy({ left: 320, behavior: "smooth" }); // scroll by ~3 ads
        }
      }
    };
    const interval = setInterval(scroll, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="overflow-hidden relative bg-gray-100 p-4">
      <div
        ref={carouselRef}
        className="flex overflow-x-auto scrollbar-hide space-x-4"
      >
        <img src="https://via.placeholder.com/300x150?text=Ad+1" alt="Ad1" className="rounded shadow" />
        <img src="https://via.placeholder.com/300x150?text=Ad+2" alt="Ad2" className="rounded shadow" />
        <img src="https://via.placeholder.com/300x150?text=Ad+3" alt="Ad3" className="rounded shadow" />
        <img src="https://via.placeholder.com/300x150?text=Ad+4" alt="Ad4" className="rounded shadow" />
        <img src="https://via.placeholder.com/300x150?text=Ad+5" alt="Ad5" className="rounded shadow" />
        <img src="https://via.placeholder.com/300x150?text=Ad+6" alt="Ad6" className="rounded shadow" />
      </div>
    </div>
  );
}

export default AdCarousel;
