import React from "react";
import { useNavigate } from "react-router-dom";

const services = [
  {
    title: "Cold Rooms",
    desc: "Custom cold & freezer rooms from small to industrial scale.",
    quoteOptions: [
      { label: "Build 5m × 10m Cold Room", value: "coldroom_5x10" },
      { label: "Build 3m × 6m Cold Room", value: "coldroom_3x6" },
    ],
  },
  {
    title: "Mobile Cooling Units",
    desc: "Mobile cold/freezer solutions for events & transport.",
    quoteOptions: [
      { label: "Build 2m × 3m Mobile Unit", value: "mobile_2x3" },
      { label: "Build 3m × 5m Mobile Unit", value: "mobile_3x5" },
    ],
  },
  {
    title: "Air Conditioning",
    desc: "Installation, repair & maintenance of AC systems.",
    quoteOptions: [
      { label: "Install New AC", value: "ac_install" },
      { label: "Service / Repair AC", value: "ac_service" },
    ],
  },
  {
    title: "Freezers & Fridges",
    desc: "High efficiency freezers, fridges & regassing.",
    quoteOptions: [
      { label: "Regas Domestic Fridge", value: "regas_fridge" },
      { label: "Repair Freezer", value: "repair_freezer" },
    ],
  },
  {
    title: "Maintenance Contracts",
    desc: "Scheduled servicing & preventative maintenance plans.",
    quoteOptions: [
      { label: "Monthly Service Plan", value: "maint_monthly" },
      { label: "Quarterly Service Plan", value: "maint_quarterly" },
    ],
  },
  {
    title: "Spare Parts & Upgrades",
    desc: "Compressors, sensors, PC boards, piping & more.",
    quoteOptions: [
      { label: "Request Spare Part", value: "spare_part" },
      { label: "System Upgrade", value: "upgrade" },
    ],
  },
];

function Services({ fullPage = false }) {
  const navigate = useNavigate();

  const handleQuote = (option) => {
    // send user to quote page with pre-filled option
    navigate(`/quote?service=${option}`);
  };

  return (
    <section id="services" className="p-8 bg-gray-50">
      <h2 className="text-2xl font-bold mb-6 text-center text-sky-600">
        Our Services
      </h2>

      <div
        className={`grid gap-6 ${
          fullPage ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3" : "grid-cols-1 md:grid-cols-3"
        }`}
      >
        {services.map((s, i) => (
          <div
            key={i}
            className="bg-white p-6 shadow-md rounded border border-sky-200 hover:shadow-lg transition"
          >
            <h3 className="text-lg font-semibold text-sky-700">{s.title}</h3>
            <p className="text-sm text-gray-600 mb-3">{s.desc}</p>

            {s.quoteOptions.map((opt, j) => (
              <button
                key={j}
                onClick={() => handleQuote(opt.value)}
                className="w-full bg-sky-500 text-white px-3 py-2 mb-2 rounded hover:bg-sky-600 transition text-sm font-bold shadow"
              >
                {opt.label}
              </button>
            ))}
          </div>
        ))}
      </div>
    </section>
  );
}

export default Services;
