import React, { useEffect, useState } from "react";

const ads = [
  "/assets/services/Aircon1.jpeg",
  "/assets/services/Coldroom1.jpeg",
  "/assets/services/Freezer1.jpeg",
  "/assets/services/WaterHeater1.jpeg",
  "/assets/services/Aircon2.jpeg",
  "/assets/services/Coldroom2.jpeg",
  "/assets/services/Freezer2.jpeg",
];

function AdCarousel() {
  const [startIndex, setStartIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setStartIndex((prev) => (prev + 1) % ads.length);
    }, 2500); // every 2.5s
    return () => clearInterval(interval);
  }, []);

  const visible = [
    ads[startIndex],
    ads[(startIndex + 1) % ads.length],
    ads[(startIndex + 2) % ads.length],
  ];

  return (
    <div className="overflow-hidden bg-gray-100 p-4">
      <div className="flex justify-center gap-4 transition-all duration-700">
        {visible.map((src, i) => (
          <img
            key={i}
            src={src}
            alt={`Ad ${i}`}
            className="w-64 h-40 object-cover rounded shadow"
          />
        ))}
      </div>
    </div>
  );
}

export default AdCarousel;
