import React from "react";

const services = [
  { title: "Cold Rooms", desc: "Custom cold storage for any scale." },
  { title: "Air Conditioning", desc: "Commercial & residential AC solutions." },
  { title: "Freezers", desc: "High efficiency freezers for all needs." },
];

function Services() {
  return (
    <section id="services" className="p-8 bg-gray-50">
      <h2 className="text-2xl font-bold mb-6 text-center">Our Services</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {services.map((s, i) => (
          <div key={i} className="bg-white p-4 shadow-md rounded">
            <h3 className="text-lg font-semibold">{s.title}</h3>
            <p className="text-sm text-gray-600">{s.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Services;
