import React, { useState, useContext, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import ProductCard from "../components/ProductCard";
import "../styles/ProductPage.css"; // Create this for styling

function ProductPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, cart } = useContext(CartContext);
  const [addedMessage, setAddedMessage] = useState(false);

  // Example: fetch product from API or static list
  const product = {
    _id: id,
    name: "Demo Product",
    description: "Full details about this product with all specifications.",
    image: "/placeholder.jpg",
    price: 1999,
  };

  // Suggested products (replace with API fetch in real app)
  const suggestedProducts = [
    { _id: "101", name: "Suggested 1", image: "/placeholder.jpg", price: 1299, description: "Description 1" },
    { _id: "102", name: "Suggested 2", image: "/placeholder.jpg", price: 1499, description: "Description 2" },
    { _id: "103", name: "Suggested 3", image: "/placeholder.jpg", price: 999, description: "Description 3" },
  ];

  const handleAddToCart = (prod) => {
    addToCart(prod);
    setAddedMessage(true);
    setTimeout(() => setAddedMessage(false), 2000); // message disappears after 2s
  };

  return (
    <div className="product-page-container">
      <div className="product-main">
        <img src={product.image} alt={product.name} className="product-image-large" />
        <div className="product-info">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">R {product.price}</p>
          <div className="product-buttons">
            <button onClick={() => handleAddToCart(product)} className="add-cart-btn">
              Add to Cart
            </button>
            <button onClick={() => navigate("/cart")} className="go-cart-btn">
              Go to Cart ({cart.length})
            </button>
            <button onClick={() => navigate("/checkout")} className="checkout-btn">
              Checkout
            </button>
          </div>
          {addedMessage && <div className="added-message">Added to cart!</div>}
        </div>
      </div>

      <h2 className="suggested-title">You might also like</h2>
      <div className="suggested-products">
        {suggestedProducts.map((prod) => (
          <ProductCard key={prod._id} product={prod} />
        ))}
      </div>
    </div>
  );
}

export default ProductPage;
