import React, { useContext, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import ProductCard from "./ProductCard";
import axios from "axios";

function ProductPage() {
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);
  const [product, setProduct] = useState(null);
  const [products, setProducts] = useState([]); // all products for suggestions
  const [added, setAdded] = useState(false);
  const navigate = useNavigate();

  // Fetch all products (same as StorePage)
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const { data } = await axios.get("/api/products");
        setProducts(data);

        // Find the clicked product by ID
        const selected = data.find((p) => p._id === id);
        setProduct(selected);
      } catch (err) {
        console.error("Error fetching products:", err);
      }
    };
    fetchProducts();
  }, [id]);

  if (!product) return <p>Loading...</p>;

  const handleAddToCart = () => {
    addToCart(product);
    setAdded(true);
    setTimeout(() => setAdded(false), 2000);
  };

  // Suggested products: 3 random products excluding current
  const suggested = products
    .filter((p) => p._id !== product._id)
    .slice(0, 3);

  return (
    <div className="product-page">
      <div className="product-page-main">
        <img
          src={product.image}
          alt={product.name}
          className="product-page-img"
        />
        <div className="product-page-info">
          <h2>{product.name}</h2>
          <p>{product.description}</p>
          <p className="product-price">R {product.price}</p>

          <button onClick={handleAddToCart} className="add-cart-btn">
            Add to Cart
          </button>
          {added && <p className="added-msg">Added to cart!</p>}

          <div className="product-actions">
            <button onClick={() => navigate("/cart")} className="go-cart-btn">
              Go to Cart
            </button>
            <button
              onClick={() => navigate("/checkout")}
              className="checkout-btn"
            >
              Checkout
            </button>
          </div>
        </div>
      </div>

      {suggested.length > 0 && (
        <div className="suggested-products">
          <h3>Suggested Products</h3>
          <div className="suggested-grid">
            {suggested.map((p) => (
              <ProductCard key={p._id} product={p} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductPage;
