import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import StageTracker from "../components/StageTracker";
import "../styles/CheckoutPage.css";

function CheckoutPage() {
  const { cart, clearCart } = useContext(CartContext);
  const [stage, setStage] = useState(1); // 1=Cart, 2=Delivery, 3=Payment, 4=Confirm
  const [delivery, setDelivery] = useState({ name: "", address: "", phone: "" });

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handlePayFast = () => {
    // Fake redirect for demo, integrate PayFast here
    alert("Redirecting to PayFast payment gateway...");
    setStage(4);
    clearCart();
  };

  return (
    <div className="checkout-page">
      <StageTracker stage={stage} />

      {stage === 1 && (
        <div>
          <h2>Review Cart</h2>
          <button onClick={() => setStage(2)}>Next: Delivery</button>
        </div>
      )}

      {stage === 2 && (
        <div className="delivery-form">
          <h2>Delivery Information</h2>
          <input
            type="text"
            placeholder="Full Name"
            value={delivery.name}
            onChange={(e) => setDelivery({ ...delivery, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Address"
            value={delivery.address}
            onChange={(e) =>
              setDelivery({ ...delivery, address: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Phone Number"
            value={delivery.phone}
            onChange={(e) => setDelivery({ ...delivery, phone: e.target.value })}
          />
          <button onClick={() => setStage(3)}>Next: Payment</button>
        </div>
      )}

      {stage === 3 && (
        <div className="payment-section">
          <h2>Payment</h2>
          <p>Total: R {total.toFixed(2)}</p>
          <button onClick={handlePayFast}>Pay with PayFast</button>
        </div>
      )}

      {stage === 4 && (
        <div className="confirmation">
          <h2>Order Confirmed ðŸŽ‰</h2>
          <p>Thank you for your order, {delivery.name}!</p>
        </div>
      )}
    </div>
  );
}

export default CheckoutPage;
