import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";

function ProductPage() {
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);

  // For now, fetch products from API or pass down via props
  const product = {
    _id: id,
    name: "Demo Product",
    description: "Full details about this product...",
    image: "/placeholder.jpg",
    price: 1999,
  };

  return (
    <div className="product-page">
      <img src={product.image} alt={product.name} className="product-page-img" />
      <div className="product-page-info">
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p className="product-price">R {product.price}</p>
        <button onClick={() => addToCart(product)} className="add-cart-btn">
          Add to Cart
        </button>
      </div>
    </div>
  );
}

export default ProductPage;
