import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import QuoteForm from "./QuoteForm";

// utility to generate numbered image paths
const generateImages = (baseName, count) =>
  Array.from({ length: count }, (_, i) => `/assets/services/${baseName}${i + 1}.jpeg`);

const services = [
  {
    title: "Air Conditioning",
    images: generateImages("Aircon", 7),
    desc: "Installation, repair & maintenance of AC systems.",
    quoteOptions: [
      { label: "Install New AC", value: "ac_install" },
      { label: "Service / Repair AC", value: "ac_service" },
    ],
  },
  {
    title: "Cold Rooms",
    images: generateImages("Coldroom", 7),
    desc: "Custom cold & freezer rooms from small to industrial scale.",
    quoteOptions: [
      { label: "Build 5m × 10m Cold Room", value: "coldroom_5x10" },
      { label: "Build 3m × 6m Cold Room", value: "coldroom_3x6" },
    ],
  },
  {
    title: "Freezers",
    images: generateImages("Freezer", 7),
    desc: "High efficiency freezers, repairs & regassing.",
    quoteOptions: [
      { label: "Regas Domestic Freezer", value: "regas_freezer" },
      { label: "Repair Freezer", value: "repair_freezer" },
    ],
  },
  {
    title: "Water Heaters",
    images: generateImages("WaterHeater", 7),
    desc: "Installation & maintenance of energy-efficient water heaters.",
    quoteOptions: [
      { label: "Install New Heater", value: "heater_install" },
      { label: "Repair Heater", value: "heater_repair" },
    ],
  },
];

function Services({ fullPage = false }) {
  const navigate = useNavigate();
  const [indexes, setIndexes] = useState(Array(services.length).fill(0));

  useEffect(() => {
    const interval = setInterval(() => {
      setIndexes((prev) =>
        prev.map((idx, i) => (idx + 1) % services[i].images.length)
      );
    }, 3000); // change image every 3s
    return () => clearInterval(interval);
  }, []);

  const handleQuote = (option) => {
    navigate(`/quote?service=${option}`);
  };

  return (
    <section id="services" className="p-8 bg-gray-50">
      <h2 className="text-2xl font-bold mb-6 text-center text-sky-600">
        Our Services
      </h2>

      <div
        className={`grid gap-6 ${
          fullPage
            ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3"
            : "grid-cols-1 md:grid-cols-2 lg:grid-cols-4"
        }`}
      >
        {services.map((s, i) => (
          <div
            key={i}
            className="bg-white p-6 shadow-md rounded border border-sky-200 hover:shadow-lg transition"
          >
            <img
              src={s.images[indexes[i]]}
              alt={s.title}
              className="w-full h-40 object-cover rounded mb-4 transition-all duration-700"
            />
            <h3 className="text-lg font-semibold text-sky-700">{s.title}</h3>
            <p className="text-sm text-gray-600 mb-3">{s.desc}</p>

            {s.quoteOptions.map((opt, j) => (
              <button
                key={j}
                onClick={() => handleQuote(opt.value)}
                className="w-full bg-sky-500 text-white px-3 py-2 mb-2 rounded hover:bg-sky-600 transition text-sm font-bold shadow"
              >
                {opt.label}
              </button>
            ))}
          </div>
        ))}
      </div>

      {fullPage && (
        <div className="mt-10">
          <QuoteForm />
        </div>
      )}
    </section>
  );
}

export default Services;
