import React from "react";
import Header from "../components/Header";
import ProductCard from "../components/ProductCard";
import "./StorePage.css";

function StorePage() {
  const categories = [
    "Airconditioner", "Appliance spares", "Compressors", "Electrical",
    "Fans", "Filter Driers", "Fridge/Cold room accessories", "Gas/ Oil",
    "Sales Items", "Shoes", "Tape/PVC/Trunking/Screws", "Tools and Multi Meters",
    "Tubing / Fitting / Armaflex", "Valves", "Aircon accessories", "Aircon spares",
    "Sensors", "Brackets", "Universal PC Board", "Universal Remote", "Condensation pump"
  ];

  const products = [
    { name: "Commercial Freezer", description: "Durable cold storage", price: 12000, image: "https://via.placeholder.com/300x200" },
    { name: "Split AC Unit", description: "Energy efficient cooling", price: 8500, image: "https://via.placeholder.com/300x200" },
    { name: "Mobile Cold Room", description: "Flexible cold storage", price: 50000, image: "https://via.placeholder.com/300x200" },
  ];

  return (
    <>
      <Header categories={categories} />
      <main className="store-page">
        <div className="product-grid">
          {products.map((p, i) => (
            <ProductCard key={i} product={p} />
          ))}
        </div>
      </main>
    </>
  );
}

export default StorePage;
