import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "../styles/QuoteForm.css";

function QuoteForm() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const serviceParam = queryParams.get("service") || "";

  const [formData, setFormData] = useState({
    service: serviceParam,
    name: "",
    email: "",
    phone: "",
    location: "",
    message: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    // Prefill service if query param changes
    setFormData((prev) => ({ ...prev, service: serviceParam }));
  }, [serviceParam]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      // Send form to backend
      await axios.post("/api/quotes", formData);
      setSubmitted(true);
      setFormData({
        service: serviceParam,
        name: "",
        email: "",
        phone: "",
        location: "",
        message: "",
      });
    } catch (err) {
      console.error(err);
      setError("Failed to submit quote. Please try again.");
    }
  };

  if (submitted) {
    return (
      <div className="quote-success">
        <h3>Thank you!</h3>
        <p>Your quote request has been sent. We will contact you soon.</p>
      </div>
    );
  }

  return (
    <form className="quote-form" onSubmit={handleSubmit}>
      <h3>Request a Quote</h3>

      <label>Service</label>
      <input
        type="text"
        name="service"
        value={formData.service}
        readOnly
      />

      <label>Name</label>
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        required
      />

      <label>Email</label>
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        required
      />

      <label>Phone</label>
      <input
        type="text"
        name="phone"
        value={formData.phone}
        onChange={handleChange}
        required
      />

      <label>Location</label>
      <input
        type="text"
        name="location"
        value={formData.location}
        onChange={handleChange}
        required
      />

      <label>Message / Details</label>
      <textarea
        name="message"
        value={formData.message}
        onChange={handleChange}
        placeholder="Describe your requirements, room dimensions, desired temperature, etc."
        rows="4"
      ></textarea>

      {error && <p className="form-error">{error}</p>}

      <button type="submit" className="quote-submit-btn">
        Submit Quote
      </button>
    </form>
  );
}

export default QuoteForm;
