import React, { useEffect, useState, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";

function ProductPage() {
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);
  const [product, setProduct] = useState(null);
  const [added, setAdded] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await fetch(`/api/products/${id}`); // make sure this endpoint exists
        const data = await res.json();
        setProduct(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    addToCart(product);
    setAdded(true);
    setTimeout(() => setAdded(false), 2000); // hide message after 2s
  };

  if (!product) return <p>Loading...</p>;

  return (
    <div className="product-page">
      <img src={product.image} alt={product.name} className="product-page-img" />
      <div className="product-page-info">
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p className="product-price">R {product.price}</p>
        <button onClick={handleAddToCart} className="add-cart-btn">
          Add to Cart
        </button>
        {added && <p className="added-msg">Added to cart!</p>}
        <div className="product-actions">
          <button onClick={() => navigate("/cart")} className="go-cart-btn">
            Go to Cart
          </button>
          <button onClick={() => navigate("/checkout")} className="checkout-btn">
            Checkout
          </button>
        </div>
      </div>
      <div className="suggested-products">
        <h3>Suggested Products</h3>
        {/* Fetch or slice 3 related products */}
      </div>
    </div>
  );
}

export default ProductPage;
